    <script>
        let distance = document.getElementById('distanceSummary')
        let price = document.getElementById('priceSummary')
        const cart = [
            <?php
            if (!empty($_SESSION['cart'])) {
                foreach ($_SESSION['cart'] as $index => $item) {
            ?>,
            {
                price: <?php echo $item['price'] ?>,
                distanceMi: <?php echo $item['distanceMi'] ?>,
                distanceKm: <?php echo $item['distanceKm'] ?>,
            },
            <?php
                }
            }
            ?>
        ];
        function displayCost(){
            let total = 0;
            cart.forEach(item => {
                total += parseFloat(item.price);
            })
            return total.toFixed(2);
        }
        
        function displayKm(){
            let total = 0;
            cart.forEach(item => {
                total += parseFloat(item.distanceKm);
            })
            return total;
        }

        function displayMi(){
            let total = 0;
            cart.forEach(item => {
                total += parseFloat(item.distanceMi);
            })
            return total;
        }
        const totalPrice = displayCost();
        const totalKm = displayKm();
        const totalMi = displayMi();
        distance.innerHTML = totalMi + `mi /  `+ totalKm + `km`; 
        price.innerHTML = `$` + totalPrice;
    </script>
